/**
* @snippet       Override Woo Checkout Fields
* @author        Kristel Lamaj
* @compatible    Woo 4.0+
*/

add_filter( 'woocommerce_checkout_fields' , 'override_fields' );

function override_fields($fields)
{

    // Billing Country
    $fields['billing']['billing_country'] = array(
        'type'      => 'select',
        'class'     => array('form-row-first'),
        'required'  => true,
        //'priority'  => 3,
        'label'     => __('Χώρα', 'woocommerce'),
        'options'   => array('GR' => 'Ελλάδα')
    );

    $fields['billing']['billing_first_name']['placeholder'] = 'Όνομα';        # Billing First Name Placeholder
    $fields['billing']['billing_last_name']['placeholder'] = 'Επίθετο';          # Billing Last Name Placeholder
    unset( $fields['billing']['billing_company'] );                                     # Unset Billing Company
    $fields['billing']['billing_address_1']['class'] = array('form-row-last');          # Billing Address 1 --> form-row-last
    unset( $fields['billing']['billing_address_2'] );                                   # Unset Billing Address 2
    $fields['billing']['billing_postcode']['class'] = array('form-row-last');          # Billing Postcode --> form-row-first
    $fields['billing']['billing_postcode']['placeholder'] = 'Ταχυδρομικός κώδικας';          # Billing Postcode Placeholder
    $fields['billing']['billing_city']['class'] = array('form-row-first');               # Billing City --> form-row-last
    $fields['billing']['billing_city']['placeholder'] = 'Πόλη';                 # Billing City Placeholder
    unset( $fields['billing']['billing_state'] );                                       # Unset Billing state
    $fields['billing']['billing_phone']['class'] = array('form-row-first');             # Billing Phone --> form-row-first
    $fields['billing']['billing_email']['class'] = array('form-row-last');              # Billing email --> form-row-last
    $fields['billing']['billing_email']['placeholder'] = 'Η διεύθυνση email σας';          # Billing email Placeholder

    //$fields['billing']['billing_first_name']['autocomplete']    = 'nope';
    //$fields['billing']['billing_last_name']['autocomplete']     = 'nope';
    //$fields['billing']['billing_address_1']['autocomplete']     = 'nope';
    //$fields['billing']['billing_city']['autocomplete']          = 'nope';
    //$fields['billing']['billing_postcode']['autocomplete']      = 'nope';
    //$fields['billing']['billing_phone']['autocomplete']         = 'nope';
    //$fields['billing']['billing_email']['autocomplete']         = 'nope';

    // Return Overrided Fields
    return $fields;
}

// PHP: Remove "(optional)" from our non required fields
add_filter( 'woocommerce_form_field' , 'remove_checkout_optional_fields_label', 10, 4 );
function remove_checkout_optional_fields_label( $field, $key, $args, $value ) {
    // Only on checkout page
    if( is_checkout() && ! is_wc_endpoint_url() ) {
        $optional = '&nbsp;<span class="optional">(' . esc_html__( 'optional', 'woocommerce' ) . ')</span>';
        $field = str_replace( $optional, '', $field );
    }
    return $field;
}

